mapper.select:
select al.id_serie, titreSerie from vw_liste_albums al inner join auteurs au on al.id_album = au.id_album and au.id_personne = #{id}
union
select s.id_serie, titreSerie from auteurs_series au inner join series s on s.id_serie = au.id_serie and au.id_personne = #{id}
union
select p.id_serie, titreSerie from auteurs_paraBD ap inner join vw_liste_paraBD p on ap.id_paraBD = p.id_paraBD and ap.id_personne = #{id}
order by 2 nulls first

mapper.sql:

update or insert into series (
    id_serie, titreSerie, terminee, suivreSorties, complete, suivreManquants, siteWeb, id_editeur,
    id_collection, sujetSerie, remarquesSerie, nb_albums, vo, couleur, etat, reliure, typeEdition,
    orientation, formatEdition, sensLecture
) values (
    #{id}, #{titreSerie}, #{terminee}, #{suivreSorties}, #{complete}, #{suivreManquants}, #{siteWeb}, #{idEditeur},
    #{idCollection}, #{sujet}, #{notes}, #{nbAlbums}, #{vo}, #{couleur}, #{etat}, #{reliure}, #{typeEdition},
    #{orientation}, #{formatEdition}, #{sensLecture}
)
